import React from 'react';

export default function ValidationError(props) {
    if(props.message){
        return (
            <div className="error">{props.message}</div>
        );
    }
    return <></>
}

ValidationError.propTypes = {
    hasError: PropTypes.bool.isRequired,
    message: PropTypes.string.isRequired
};
